// ...existing code...
import React, { useState } from "react";

export default function App() {
  const [user, setUser] = useState("");
  const [pass, setPass] = useState("");
  const [msg, setMsg] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    // demo credentials check (replace with real API call)
    if (user === "kmadhukumar25@gmail.com" && pass === "Madhu143") {
      setMsg("Login successful (demo).");
    } else {
      setMsg("Invalid credentials (demo).");
    }
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 24 }}>
      <h1>React Python Auth (demo)</h1>
      <form onSubmit={handleSubmit} style={{ maxWidth: 360 }}>
        <label>
          Email
          <input
            style={{ display: "block", width: "100%", margin: "8px 0 16px" }}
            value={user}
            onChange={(e) => setUser(e.target.value)}
            placeholder="email"
          />
        </label>
        <label>
          Password
          <input
            type="password"
            style={{ display: "block", width: "100%", margin: "8px 0 16px" }}
            value={pass}
            onChange={(e) => setPass(e.target.value)}
            placeholder="password"
          />
        </label>
        <button type="submit">Sign in</button>
      </form>
      {msg && <p>{msg}</p>}
    </div>
  );
}
