// ...existing code...
import React, { useState } from "react";

export default function App() {
  const [user, setUser] = useState("");
  const [pass, setPass] = useState("");
  const [msg, setMsg] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // demo project counts (replace with API data later)
  const [totalProjects, setTotalProjects] = useState(3);
  const [completedProjects, setCompletedProjects] = useState(1);

  // new states for project creation / navigation
  const [isCreatingProject, setIsCreatingProject] = useState(false);
  const [projectName, setProjectName] = useState("");
  const [savedProjectName, setSavedProjectName] = useState("");
  const [selectedModule, setSelectedModule] = useState("BRD Generation");
  const [documentGenerator, setDocumentGenerator] = useState("");

  const MODULES = [
    "BRD Generation",
    "FRD Generation",
    "SRS Generation",
    "Impact Analysis",
    "Requirement Priortization",
    "Wireframe Generator",
    "Prototype Generator",
  ];

  const DOC_OPTIONS = [
    "BRD Creation",
    "FRD Creation",
    "SRS Creation",
    "Impact Analysis",
    "Requirement Priortization",
    "Wire Frame Generator",
    "Prototype Generator",
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    if (user === "kmadhukumar25@gmail.com" && pass === "Madhu143") {
      setMsg("Login successful.");
      setIsLoggedIn(true);
    } else {
      setMsg("Invalid credentials.");
    }
  };

  const handleAddProject = () => {
    // open new-project page
    setProjectName("");
    setSavedProjectName("");
    setDocumentGenerator("");
    setSelectedModule(MODULES[0]);
    setIsCreatingProject(true);
  };

  // Save project but stay on the new-project page and display name
  const handleSaveProject = () => {
    if (!projectName.trim()) {
      setMsg("Please enter a project name.");
      setTimeout(() => setMsg(""), 2500);
      return;
    }
    setSavedProjectName(projectName.trim());
    setTotalProjects((n) => n + 1);
    setMsg("Project saved.");
    setTimeout(() => setMsg(""), 1800);
  };

  const handleCancelCreate = () => {
    setIsCreatingProject(false);
  };

  const handleCompleteProject = () => {
    setCompletedProjects((c) => Math.min(c + 1, totalProjects));
  };

  const handleLogout = () => {
    // reset UI state and go back to login
    setIsLoggedIn(false);
    setIsCreatingProject(false);
    setUser("");
    setPass("");
    setMsg("");
    setProjectName("");
    setSavedProjectName("");
    setDocumentGenerator("");
  };

  const pageStyle = {
    minHeight: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    background: "linear-gradient(180deg,#f5f7fa 0%,#eef2f6 100%)",
    fontFamily: "Inter, Roboto, Arial, sans-serif",
    padding: 24,
  };

  const cardStyle = {
    background: "#ffffff",
    padding: 24,
    borderRadius: 12,
    boxShadow: "0 8px 28px rgba(16,24,40,0.08)",
    width: 300,
    maxWidth: "94%",
    color: "#111827",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  };

  const gridStyle = {
    display: "grid",
    gridTemplateColumns: "1fr 1fr",
    gap: 20,
    marginTop: 20,
  };

  const headerStyle = {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    marginBottom: 6,
  };

  const inputStyle = {
    display: "block",
    width: "100%",
    padding: "10px 12px",
    margin: "8px 0 16px",
    borderRadius: 8,
    border: "1px solid #e6e9ef",
    fontSize: 14,
    outline: "none",
  };

  const labelStyle = { fontSize: 13, color: "#374151", fontWeight: 600 };

  const buttonStyle = {
    width: "100%",
    padding: "10px 12px",
    background: "#0f172a",
    color: "#ffffff",
    border: "none",
    borderRadius: 8,
    cursor: "pointer",
    fontWeight: 600,
    fontSize: 15,
  };

  const topButton = {
    background: "#0f172a",
    color: "#fff",
    border: "none",
    padding: "8px 14px",
    borderRadius: 8,
    cursor: "pointer",
    fontWeight: 600,
  };

  // Login screen
  if (!isLoggedIn) {
    return (
      <div style={pageStyle}>
        <div style={{ ...cardStyle, width: 420, padding: 32 }}>
          <h1 style={{ margin: 0, fontSize: 20 }}>BA Assistant Tool</h1>
          <p style={{ marginTop: 8, marginBottom: 20, color: "#6b7280", fontSize: 13 }}>
            Sign in to continue
          </p>

          <form onSubmit={handleSubmit} aria-label="login form">
            <label style={labelStyle}>
              Email
              <input
                style={inputStyle}
                value={user}
                onChange={(e) => setUser(e.target.value)}
                placeholder="name@company.com"
                type="email"
                required
              />
            </label>

            <label style={labelStyle}>
              Password
              <input
                type="password"
                style={inputStyle}
                value={pass}
                onChange={(e) => setPass(e.target.value)}
                placeholder="Enter your password"
                required
              />
            </label>

            <button type="submit" style={buttonStyle}>
              Sign in
            </button>
          </form>

          {msg && <p style={{ marginTop: 16, color: msg.includes("successful") ? "#059669" : "#dc2626" }}>{msg}</p>}
        </div>
      </div>
    );
  }

  // New Project creation page
  if (isCreatingProject) {
    return (
      <div style={{ minHeight: "100vh", background: "#f4f6f8", padding: 20, fontFamily: "Inter, Roboto, Arial, sans-serif" }}>
        <div style={{ maxWidth: 1200, margin: "0 auto", display: "flex", gap: 24 }}>
          {/* Left sidebar modules */}
          <aside style={{ width: 280, background: "#fff", borderRadius: 12, padding: 18, boxShadow: "0 6px 18px rgba(16,24,40,0.06)" }}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 12 }}>
              <div style={{ fontWeight: 700 }}>Modules</div>
              <button onClick={() => setIsCreatingProject(false)} style={{ background: "transparent", border: "none", cursor: "pointer", color: "#6b7280" }}>
                Close
              </button>
            </div>

            <nav>
              {MODULES.map((m) => (
                <div
                  key={m}
                  onClick={() => setSelectedModule(m)}
                  style={{
                    padding: "10px 12px",
                    borderRadius: 8,
                    cursor: "pointer",
                    marginBottom: 8,
                    background: selectedModule === m ? "#0f172a" : "transparent",
                    color: selectedModule === m ? "#fff" : "#111827",
                    fontWeight: selectedModule === m ? 700 : 600,
                  }}
                >
                  {m}
                </div>
              ))}
            </nav>
          </aside>

          {/* Main content for selected module (Project Name moved here) */}
          <main style={{ flex: 1 }}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 12 }}>
              <div style={{ display: "flex", alignItems: "center", gap: 12 }}>
                <button onClick={() => setIsCreatingProject(false)} aria-label="Back to dashboard" style={{ background: "transparent", border: "none", cursor: "pointer", fontSize: 18 }}>
                  ←
                </button>
                <div>
                  <div style={{ fontSize: 14, color: "#6b7280", fontWeight: 600 }}>{selectedModule}</div>
                  <div style={{ marginTop: 6, fontSize: 18, fontWeight: 700, color: "#0f172a" }}>{savedProjectName || "Untitled Project"}</div>
                </div>
              </div>

              <div style={{ display: "flex", gap: 10, alignItems: "center" }}>
                <input
                  value={projectName}
                  onChange={(e) => setProjectName(e.target.value)}
                  placeholder="Project Name"
                  style={{ ...inputStyle, width: 300, margin: 0 }}
                />
                <button onClick={handleSaveProject} style={{ ...topButton, padding: "8px 12px" }}>
                  Save
                </button>
                <button onClick={handleLogout} style={{ background: "transparent", border: "1px solid #e6e9ef", padding: "8px 12px", borderRadius: 8, cursor: "pointer" }}>
                  Logout
                </button>
              </div>
            </div>

            <div style={{ background: "#fff", borderRadius: 12, padding: 20, boxShadow: "0 6px 18px rgba(16,24,40,0.06)" }}>
              {/* show saved project name and document generator dropdown after save */}
              <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 12 }}>
                <div>
                  <div style={{ fontSize: 13, color: "#6b7280", fontWeight: 600 }}>Project</div>
                  <div style={{ marginTop: 6, fontSize: 18, fontWeight: 700, color: "#0f172a" }}>{savedProjectName || "No project saved yet"}</div>
                </div>
              </div>

              <section style={{ marginTop: 12, color: "#374151" }}>
                <label style={{ display: "block", fontSize: 13, color: "#374151", marginBottom: 8 }}>Document Generator</label>
                <select
                  value={documentGenerator}
                  onChange={(e) => setDocumentGenerator(e.target.value)}
                  style={{ ...inputStyle, padding: "10px 12px", width: 360 }}
                >
                  <option value="">Select document</option>
                  {DOC_OPTIONS.map((d) => (
                    <option key={d} value={d}>
                      {d}
                    </option>
                  ))}
                </select>

                <div style={{ marginTop: 18 }}>
                  <textarea
                    placeholder={documentGenerator ? `${documentGenerator} editor` : "Select a document type to start editing"}
                    style={{ width: "100%", minHeight: 220, borderRadius: 8, border: "1px solid #e6e9ef", padding: 12 }}
                  />
                </div>
              </section>
            </div>
          </main>
        </div>
        {msg && <div style={{ position: "fixed", bottom: 20, right: 20, background: "#111827", color: "#fff", padding: "10px 14px", borderRadius: 8 }}>{msg}</div>}
      </div>
    );
  }

  // Dashboard screen
  return (
    <div style={{ minHeight: "100vh", background: "#f4f6f8", padding: 28, fontFamily: "Inter, Roboto, Arial, sans-serif" }}>
      <div style={{ maxWidth: 1100, margin: "0 auto" }}>
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 18 }}>
          <h2 style={{ margin: 0, color: "#0f172a" }}>BA Assistant Tool</h2>
          <div style={{ display: "flex", gap: 10 }}>
            <button style={topButton} onClick={handleAddProject}>
              + New Project
            </button>
            <button onClick={handleLogout} style={{ background: "transparent", border: "1px solid #e6e9ef", padding: "8px 12px", borderRadius: 8, cursor: "pointer" }}>
              Logout
            </button>
          </div>
        </div>

        <div style={gridStyle}>
          <div style={cardStyle}>
            <div>
              <div style={headerStyle}>
                <div>
                  <div style={{ fontSize: 13, color: "#6b7280", fontWeight: 600 }}>Total Projects</div>
                  <div style={{ marginTop: 8, fontSize: 22, fontWeight: 700, color: "#111827" }}>Projects overview</div>
                </div>
                <div>
                  <button onClick={handleAddProject} style={{ ...topButton, padding: "6px 10px", fontSize: 13 }}>
                    Add
                  </button>
                </div>
              </div>
            </div>

            <div style={{ marginTop: 18, textAlign: "center", paddingTop: 12, borderTop: "1px solid #eef2f6" }}>
              <div style={{ fontSize: 28, fontWeight: 800 }}>{totalProjects}</div>
              <div style={{ marginTop: 6, color: "#6b7280", fontSize: 13 }}>Total projects</div>
            </div>
          </div>

          <div style={cardStyle}>
            <div>
              <div style={headerStyle}>
                <div>
                  <div style={{ fontSize: 13, color: "#6b7280", fontWeight: 600 }}>Completed Projects</div>
                  <div style={{ marginTop: 8, fontSize: 22, fontWeight: 700, color: "#111827" }}>Completion status</div>
                </div>
                <div>
                  <button onClick={handleCompleteProject} style={{ background: "#047857", color: "#fff", padding: "6px 10px", borderRadius: 8, border: "none", cursor: "pointer", fontWeight: 600, fontSize: 13 }}>
                    Mark Complete
                  </button>
                </div>
              </div>
            </div>

            <div style={{ marginTop: 18, textAlign: "center", paddingTop: 12, borderTop: "1px solid #eef2f6" }}>
              <div style={{ fontSize: 28, fontWeight: 800, color: "#059669" }}>{completedProjects}</div>
              <div style={{ marginTop: 6, color: "#6b7280", fontSize: 13 }}>Completed projects</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
