import React, { useState } from 'react';
import axios from 'axios';

const WireframeGenerator = ({ 
  projectName, 
  frdContent = '', 
  userStories = [], 
  onWireframeGenerated 
}) => {
  const [isGenerating, setIsGenerating] = useState(false);
  const [wireframeHtml, setWireframeHtml] = useState('');
  const [error, setError] = useState('');
  const [domain, setDomain] = useState('generic');

  const domains = [
    'generic', 'healthcare', 'education', 'ecommerce', 
    'banking', 'marketing', 'logistics', 'insurance'
  ];

  const generateWireframes = async () => {
    if (!projectName) {
      setError('Project name is required');
      return;
    }

    if (!frdContent && (!userStories || userStories.length === 0)) {
      setError('Either FRD content or user stories are required');
      return;
    }

    setIsGenerating(true);
    setError('');

    try {
      const payload = {
        project: projectName,
        domain: domain,
        ...(frdContent ? { frd_content: frdContent } : { user_stories: userStories })
      };

      console.log('üîÑ Generating wireframes...', payload);

      const response = await axios.post(
        'http://localhost:8001/ai/wireframes',
        payload,
        {
          timeout: 30000,
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );

      const result = response.data;
      const htmlContent = result.html || '';

      setWireframeHtml(htmlContent);
      
      // Save wireframes to a downloadable file
      const blob = new Blob([htmlContent], { type: 'text/html' });
      const url = URL.createObjectURL(blob);
      
      // Auto-download the wireframe file
      const link = document.createElement('a');
      link.href = url;
      link.download = `${projectName.replace(/\s+/g, '_')}_wireframes.html`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);

      if (onWireframeGenerated) {
        onWireframeGenerated(htmlContent, result.domain);
      }

      console.log('‚úÖ Wireframes generated successfully!');

    } catch (err) {
      console.error('‚ùå Error generating wireframes:', err);
      setError(
        err.response?.data?.detail || 
        err.message || 
        'Failed to generate wireframes. Please try again.'
      );
    } finally {
      setIsGenerating(false);
    }
  };

  const downloadWireframes = () => {
    if (!wireframeHtml) return;

    const blob = new Blob([wireframeHtml], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    
    const link = document.createElement('a');
    link.href = url;
    link.download = `${projectName.replace(/\s+/g, '_')}_wireframes.html`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const previewWireframes = () => {
    if (!wireframeHtml) return;

    // Open wireframes in a new window for preview
    const newWindow = window.open('', '_blank');
    if (newWindow) {
      newWindow.document.write(wireframeHtml);
      newWindow.document.close();
    }
  };

  return (
    <div className="wireframe-generator">
      <div className="wireframe-header">
        <h2>üé® Wireframe Generator</h2>
        <p>Generate interactive wireframes from user stories or FRD content</p>
      </div>

      <div className="wireframe-controls">
        <div className="domain-selector">
          <label htmlFor="domain-select">Domain:</label>
          <select 
            id="domain-select"
            value={domain} 
            onChange={(e) => setDomain(e.target.value)}
            className="domain-dropdown"
          >
            {domains.map(d => (
              <option key={d} value={d}>
                {d.charAt(0).toUpperCase() + d.slice(1)}
              </option>
            ))}
          </select>
        </div>

        <button
          onClick={generateWireframes}
          disabled={isGenerating || !projectName}
          className={`generate-button ${isGenerating ? 'generating' : ''}`}
        >
          {isGenerating ? (
            <>
              <span className="spinner"></span>
              Generating Wireframes...
            </>
          ) : (
            'Generate Wireframes'
          )}
        </button>
      </div>

      {error && (
        <div className="error-message">
          ‚ùå {error}
        </div>
      )}

      {wireframeHtml && (
        <div className="wireframe-results">
          <div className="results-header">
            <h3>‚úÖ Wireframes Generated Successfully!</h3>
            <p>Interactive wireframes with {Math.floor(wireframeHtml.length / 1000)}K+ characters</p>
          </div>

          <div className="wireframe-actions">
            <button onClick={previewWireframes} className="preview-button">
              üëÅÔ∏è Preview Wireframes
            </button>
            <button onClick={downloadWireframes} className="download-button">
              üíæ Download HTML File
            </button>
          </div>

          <div className="wireframe-preview">
            <h4>Features Generated:</h4>
            <ul className="feature-list">
              {wireframeHtml.includes('login') && <li>üîê Login Page</li>}
              {wireframeHtml.includes('dashboard') && <li>üìä Dashboard</li>}
              {wireframeHtml.includes('form-field') && <li>üìù Forms</li>}
              {wireframeHtml.includes('nav-item') && <li>üß≠ Navigation</li>}
              {wireframeHtml.includes('onclick') && <li>‚ö° Interactive Elements</li>}
              {wireframeHtml.includes('responsive') && <li>üì± Responsive Design</li>}
            </ul>
          </div>
        </div>
      )}

      <style jsx>{`
        .wireframe-generator {
          background: white;
          border-radius: 8px;
          padding: 24px;
          margin: 20px 0;
          box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
        }

        .wireframe-header {
          text-align: center;
          margin-bottom: 24px;
        }

        .wireframe-header h2 {
          color: #2d3748;
          margin-bottom: 8px;
        }

        .wireframe-header p {
          color: #718096;
        }

        .wireframe-controls {
          display: flex;
          gap: 16px;
          align-items: center;
          justify-content: center;
          margin-bottom: 20px;
          flex-wrap: wrap;
        }

        .domain-selector {
          display: flex;
          align-items: center;
          gap: 8px;
        }

        .domain-dropdown {
          padding: 8px 12px;
          border: 1px solid #e2e8f0;
          border-radius: 6px;
          background: white;
          font-size: 14px;
          min-width: 120px;
        }

        .generate-button {
          background: #4299e1;
          color: white;
          border: none;
          padding: 12px 24px;
          border-radius: 6px;
          font-size: 16px;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.2s;
          display: flex;
          align-items: center;
          gap: 8px;
        }

        .generate-button:hover:not(:disabled) {
          background: #3182ce;
          transform: translateY(-1px);
        }

        .generate-button:disabled {
          background: #a0aec0;
          cursor: not-allowed;
          transform: none;
        }

        .generate-button.generating {
          background: #805ad5;
        }

        .spinner {
          width: 16px;
          height: 16px;
          border: 2px solid transparent;
          border-top: 2px solid white;
          border-radius: 50%;
          animation: spin 1s linear infinite;
        }

        @keyframes spin {
          to { transform: rotate(360deg); }
        }

        .error-message {
          background: #fed7d7;
          color: #c53030;
          padding: 12px;
          border-radius: 6px;
          border: 1px solid #feb2b2;
          margin: 16px 0;
          text-align: center;
        }

        .wireframe-results {
