        }

        .results-header h3 {
          color: #38a169;
          margin-bottom: 8px;
        }

        .results-header p {
          color: #718096;
          font-size: 14px;
        }

        .wireframe-actions {
          display: flex;
          gap: 12px;
          justify-content: center;
          margin-bottom: 20px;
          flex-wrap: wrap;
        }

        .preview-button, .download-button {
          padding: 10px 20px;
          border: none;
          border-radius: 6px;
          font-size: 14px;
          font-weight: 500;
          cursor: pointer;
          transition: all 0.2s;
        }

        .preview-button {
          background: #805ad5;
          color: white;
        }

        .preview-button:hover {
          background: #6b46c1;
        }

        .download-button {
          background: #38a169;
          color: white;
        }

        .download-button:hover {
          background: #2f855a;
        }

        .wireframe-preview {
          background: white;
          padding: 16px;
          border-radius: 6px;
          border: 1px solid #e2e8f0;
        }

        .wireframe-preview h4 {
          color: #2d3748;
          margin-bottom: 12px;
        }

        .feature-list {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
