// ...existing code...
import React, { useState } from "react";

export default function App() {
  const [user, setUser] = useState("");
  const [pass, setPass] = useState("");
  const [msg, setMsg] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (user === "kmadhukumar25@gmail.com" && pass === "Madhu143") {
      setMsg("Login successful (demo).");
    } else {
      setMsg("Invalid credentials (demo).");
    }
  };

  const pageStyle = {
    minHeight: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    background: "linear-gradient(180deg,#f5f7fa 0%,#eef2f6 100%)",
    fontFamily: "Inter, Roboto, Arial, sans-serif",
    padding: 24,
  };

  const cardStyle = {
    background: "#ffffff",
    padding: 32,
    borderRadius: 12,
    boxShadow: "0 8px 28px rgba(16,24,40,0.08)",
    width: 420,
    maxWidth: "94%",
    color: "#111827",
  };

  const inputStyle = {
    display: "block",
    width: "100%",
    padding: "10px 12px",
    margin: "8px 0 16px",
    borderRadius: 8,
    border: "1px solid #e6e9ef",
    fontSize: 14,
    outline: "none",
  };

  const labelStyle = { fontSize: 13, color: "#374151", fontWeight: 600 };

  const buttonStyle = {
    width: "100%",
    padding: "10px 12px",
    background: "#0f172a",
    color: "#ffffff",
    border: "none",
    borderRadius: 8,
    cursor: "pointer",
    fontWeight: 600,
    fontSize: 15,
  };

  const msgStyle = { marginTop: 16, color: msg.includes("successful") ? "#059669" : "#dc2626" };

  return (
    <div style={pageStyle}>
      <div style={cardStyle}>
        <h1 style={{ margin: 0, fontSize: 20 }}>BA Assistant Tool</h1>
        <p style={{ marginTop: 8, marginBottom: 20, color: "#6b7280", fontSize: 13 }}>
          Sign in to continue
        </p>

        <form onSubmit={handleSubmit} aria-label="login form">
          <label style={labelStyle}>
            Email
            <input
              style={inputStyle}
              value={user}
              onChange={(e) => setUser(e.target.value)}
              placeholder="name@company.com"
              type="email"
              required
            />
          </label>

          <label style={labelStyle}>
            Password
            <input
              type="password"
              style={inputStyle}
              value={pass}
              onChange={(e) => setPass(e.target.value)}
              placeholder="Enter your password"
              required
            />
          </label>

          <button type="submit" style={buttonStyle}>
            Sign in
          </button>
        </form>

        {msg && <p style={msgStyle}>{msg}</p>}
      </div>
    </div>
  );
}
