  // Helper functions for FRD generation
  const extractRequirementsFromBrd = (brdText) => {
    // Extract requirements from BRD text
    const lines = brdText.split('\n').map(line => line.trim()).filter(line => line);
    const requirements = [];

    let currentSection = '';
    for (const line of lines) {
      if (line.toLowerCase().includes('requirement') || 
          line.toLowerCase().includes('functionality') ||
          line.toLowerCase().includes('feature')) {
        if (line.length > 10 && !line.includes('Document') && !line.includes('document')) {
          requirements.push({
            text: line,
            section: currentSection,
            priority: determinePriority(line)
          });
        }
      }

      if (line.includes(':') && line.length < 50) {
        currentSection = line.replace(':', '').trim();
      }
    }

    // If no requirements found, create generic ones
    if (requirements.length === 0) {
      requirements.push(
        { text: 'System must provide user authentication and authorization', section: 'Core', priority: 'High' },
        { text: 'System must support data management and processing', section: 'Core', priority: 'High' },
        { text: 'System must provide reporting and analytics capabilities', section: 'Reporting', priority: 'Medium' }
      );
    }

    return requirements;
  };

  const convertRequirementsToEpics = (requirements, domain) => {
    const epics = [];
    const groupedReqs = groupRequirementsByFeature(requirements);

    Object.keys(groupedReqs).forEach((feature, index) => {
      const epicId = `EPIC-${String(index + 1).padStart(2, '0')}`;
      const reqs = groupedReqs[feature];

      epics.push({
        id: epicId,
        title: `${feature} Management`,
        description: `Implement comprehensive ${feature.toLowerCase()} functionality for ${domain} domain`,
        businessValue: `Enables efficient ${feature.toLowerCase()} operations and improves ${domain} business processes`,
        acceptanceCriteria: [
          `${feature} functionality is fully implemented and tested`,
          `All ${domain}-specific requirements are met`,
          `Performance meets industry standards`,
          `Security and compliance requirements are satisfied`
        ],
        functionalRequirements: reqs.map(req => req.text),
        priority: reqs.some(r => r.priority === 'High') ? 'High' : 'Medium'
      });
    });

    return epics;
  };

  const convertEpicsToUserStories = (epics, domain) => {
    const userStories = [];

    epics.forEach((epic, epicIndex) => {
      // Create 2-3 user stories per epic
      const storyCount = Math.min(3, Math.max(2, epic.functionalRequirements.length));

      for (let i = 0; i < storyCount; i++) {
        const storyId = `US-${String(epicIndex + 1).padStart(2, '0')}-${String(i + 1).padStart(2, '0')}`;
        const persona = getDomainPersona(domain, i);

        userStories.push({
          id: storyId,
          title: `${epic.title} - ${getStoryAction(i)}`,
          asA: persona,
          iWant: getStoryWant(epic.title, i),
          soThat: getStorySoThat(domain, i),
          acceptanceCriteria: [
            `${persona} can successfully ${getStoryAction(i).toLowerCase()}`,
            `System validates all inputs and provides feedback`,
            `${domain}-specific business rules are enforced`,
            `Performance meets acceptable standards`
          ],
          validationCriteria: getStoryValidationCriteria(domain, epic.title, getStoryAction(i), persona),
          priority: epic.priority,
          storyPoints: getStoryPoints(i),
          epicId: epic.id
        });
      }
    });

    return userStories;
  };

  // Helper functions for story generation
  const groupRequirementsByFeature = (requirements) => {
    const groups = {};

    requirements.forEach(req => {
      let feature = 'Core System';

      if (req.text.toLowerCase().includes('user') || req.text.toLowerCase().includes('auth')) {
        feature = 'User Management';
      } else if (req.text.toLowerCase().includes('data') || req.text.toLowerCase().includes('process')) {
        feature = 'Data Processing';
      } else if (req.text.toLowerCase().includes('report') || req.text.toLowerCase().includes('analytic')) {
        feature = 'Reporting';
      } else if (req.section && req.section !== '') {
        feature = req.section;
      }

      if (!groups[feature]) groups[feature] = [];
      groups[feature].push(req);
    });

    return groups;
  };

  const determinePriority = (text) => {
    if (text.toLowerCase().includes('must') || text.toLowerCase().includes('critical')) return 'High';
    if (text.toLowerCase().includes('should') || text.toLowerCase().includes('important')) return 'Medium';
    return 'Low';
