      const existingCount = p.documents.filter((d) => d.type === docType).length;
      const version = existingCount + 1;
      const doc = {
        id: `${docType.replace(/\s/g, "_")}_${Date.now()}`,
        type: docType,
        version,
        html: htmlContent,
        approved,
        createdAt: new Date().toISOString(),
      };
      p.documents = [...p.documents, doc];
      newPrev[project] = p;
      return newPrev;
    });
